mapper.insert:
INSERT INTO basket(
				product_detail_no,
				count

					,member_no


					,guest_session_id

				)
				VALUES(#{productDetailNo},
				#{count}

					,#{memberNo}


					,#{guestSessionId}

				)

mapper.update:
UPDATE basket 
	SET count = #{count}  
WHERE basket_no = #{basketNo}

mapper.select:
		SELECT
			basket_no, a.product_detail_no , count, b.product_no, option_no, option_name, option_value, product_name, description, price, regdate
			FROM basket a, product_detail b, shoppingmall.option c, product d
			WHERE 
					a.product_detail_no = b.product_detail_no
                AND a.product_detail_no = c.product_detail_no 
                AND b.product_no = d.product_no 
                AND member_no = #{memberNo} 
				AND session_id = #{sessionId}
				AND a.product_detail_no = #{productDetailNo} 

		ORDER BY basket_no;
