mapper.insert:
INSERT INTO basket(
				item_no,
				count

					,member_no


					,session_id

				)
				VALUES(#{itemNo},
				#{count}

					,#{memberNo}


					,#{sessionId}

				)

mapper.update:
UPDATE basket 
	SET count = #{count}  
WHERE basket_no = #{basketNo}

mapper.select:
		SELECT
			basket_no, a.item_no , count, b.product_no, option_no, option_name, option_value, product_name, description, price, regdate
			FROM basket a, item b, shoppingmall.option c, product d
			WHERE 
					a.item_no = b.item_no
                AND a.item_no = c.item_no 
                AND b.product_no = d.product_no 
                AND member_no = #{memberNo} 
				AND session_id = #{sessionId}
				AND a.item_no = #{itemNo} 

		ORDER BY basket_no;
